sys_script_include.script:
var Utils = Class.create();

BUTTON_TABLE = 'x_snc_mocha_button';
CASE_TABLE = 'x_snc_mocha_case';

Utils.prototype = {
	initialize: function() {
	},

	/*
	 * check if the button has recently been pressed in this location
	 *
	 */
	openRecordExists : function(buttonSerialNumber) {

		var buttonGr = this._getButtonFromSerialNumber(butonSerialNumber);

		var caseGr = new GlideRecord(CASE_TABLE);

		caseGr.addQuery('active', true);
		caseGr.addQuery('button', buttonGr.getUniqueValue());
		caseGr.query();

		if (caseGr.next()) {
			return caseGr;
		}

		return;

	},

	incrementClickCount : function(caseGr) {

		var countStr = caseGr.getValue('click_count');
		var countVal = parseInt(countStr, 10);
		++countVal;

		caseGr.click_count = countVal;
		caseGr.update();

		return countVal;

	},

	createCase : function(buttonSerialNumber, clickType) {

		var buttonGr = this._getButtonFromSerialNumber(butonSerialNumber);

		var caseGr = new GlideRecord(CASE_TABLE);

		caseGr.newRecord();
		caseGr.button = buttonGr.getUniqueValue();
		caseGr.click_type = clickType;
		caseGr.short_description = buttonGr.getDisplayValue() + ' clicked. Service needed at ' + buttonGr.location.getDisplayValue();

	},

	_getButtonFromSerialNumber : function(buttonSerialNumber) {

		var buttonGr = new GlideRecord(BUTTON_TABLE);

		buttonGr.get('serial_number', buttonSerialNumber);

		return buttonGr;

	},

	type: 'Utils'
};
