sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	/*
	 * Sample input:
	 * { "serialNumber": "G030MD049095UCHW",
	 *   "batteryVoltage": "1697mV",
	 *	 "clickType": "DOUBLE"
	 * }
	 */

	gs.info('Mocha Dash Scripted REST API : ' + JSON.stringify(request.body.data));

	var serial_number = request.body.data.serialNumber;
	var voltage = request.body.data.batteryVoltage;
	var click_type = request.body.data.clickType;
	// Convert voltage from string to number
	var voltStr = voltage.replace('mV', '');
	var voltInt = parseInt(voltStr, 10);
	var answer = {};
	var httpStatusCode = 500;
	var httpStatusBody = 'Server Error';
	var mu = new Utils();

	// Validate the button is registered
	var buttonGr = mu.getButtonFromSerialNumber(serial_number);

	if (!buttonGr) {
		gs.error('Mocha Dash Scripted REST API: Unregistered button: ' + serial_number);

		answer = {'app' : 'Mocha', 'status' : 'UNREG'};
		httpStatusCode = 503;

	} else {
		gs.info('Mocha Dash Scripted REST API: Registered button ' + buttonGr.getDisplayValue());


		gs.info('>>>Mocha: click_type=' + click_type);

		if (click_type == 'SINGLE') {
			var caseGr = mu.openCaseExists(buttonGr);

			if (caseGr) {
				gs.info('>>>Mocha: case exists ' + caseGr.getDisplayValue());
				mu.incrementClickCount(caseGr);
				httpStatusCode = 200;
			} else {
				gs.info('>>>Mocha: no case - creating new one');
				mu.createCase(buttonGr, click_type);
				httpStatusCode = 201;
			}

		} else {
			gs.info('>>>Mocha: closing case');
			mu.closeCase(buttonGr);
			httpStatusCode = 200;
		}
		httpStatusBody = 'OK';
		answer = {'app' : 'Mocha', 'status' : httpStatusBody};

	}

	response.setStatus(httpStatusCode);
	response.setBody(answer);

})(request, response);
